import React, { useState } from "react";
import { useNavigate } from "react-router";

export default function Create() {
  const [form, setForm] = useState({ name: "", position: "Intern" });
  const navigate = useNavigate();

  const updateForm = (field, value) => {
    setForm((prevForm) => ({ ...prevForm, [field]: value }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    try {
      await fetch("http://localhost:5000/record/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(form),
      });

      setForm({ name: "", position: "Intern" });
      navigate("/");
    } catch (error) {
      window.alert(error);
    }
  };

  return (
    <div>
      <h3>Create New Record</h3>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={form.name}
            onChange={(e) => updateForm("name", e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="position">Position</label>
          <input
            type="text"
            className="form-control"
            id="position"
            value={form.position}
            onChange={(e) => updateForm("position", e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Level</label>
          <div>
            <input
              type="radio"
              id="positionIntern"
              value="Intern"
              checked={form.position === "Intern"}
              onChange={() => updateForm("position", "Intern")}
            />
            <label htmlFor="positionIntern">Intern</label>
          </div>
          <div>
            <input
              type="radio"
              id="positionJunior"
              value="Junior"
              checked={form.position === "Junior"}
              onChange={() => updateForm("position", "Junior")}
            />
            <label htmlFor="positionJunior">Junior</label>
          </div>
          <div>
            <input
              type="radio"
              id="positionSenior"
              value="Senior"
              checked={form.position === "Senior"}
              onChange={() => updateForm("position", "Senior")}
            />
            <label htmlFor="positionSenior">Senior</label>
          </div>
        </div>
        <div className="form-group">
          <input
            type="submit"
            value="Create person"
            className="btn btn-primary"
          />
        </div>
      </form>
    </div>
  );
}
