import React, { useState, useEffect } from "react";
// We use Route in order to define the different routes of our application
import { Route, Routes } from "react-router-dom";
 // We import all the components we need in our app
import Navbar from "./components/navbar";
import RecordList from "./components/recordList";
import Edit from "./components/edit";
import Create from "./components/create";
import SpotifyData from "./components/spotifyData";//spotify API data page
const App = () => {
  // Assuming you fetch top tracks and set them in a state
  const [topTracks, setTopTracks] = useState([]);

  // Example: Fetch top tracks from your API endpoint
  useEffect(() => {
    const fetchTopTracks = async () => {
      try {
        const response = await fetch('/spotifyData'); // Adjust the URL based on your server setup
        const data = await response.json();
        setTopTracks(data);
      } catch (error) {
        console.error('Error fetching top tracks:', error);
      }
    };

    fetchTopTracks();
  }, []); // Run the effect only once on component mount

 return (
   <div>
     <Navbar />
     <Routes>
       <Route exact path="/" element={<RecordList />} />
       <Route path="/edit/:id" element={<Edit />} />
       <Route path="/create" element={<Create />} />
       <Route path="/spotifyData" element={<SpotifyData />} />
     </Routes>
   </div>
 );
};
 export default App;
